import{_ as r,v as t,b as a,R as e}from"./chunks/framework.4f207390.js";const U=JSON.parse('{"title":"平面设计和UI设计有什么区别？","description":"","frontmatter":{},"headers":[],"relativePath":"其他/技术博客/平面设计和UI设计有什么区别.md","filePath":"其他/技术博客/平面设计和UI设计有什么区别.md","lastUpdated":1676540167000}'),o={name:"其他/技术博客/平面设计和UI设计有什么区别.md"},s=e('<h1 id="平面设计和ui设计有什么区别" tabindex="-1">平面设计和UI设计有什么区别？ <a class="header-anchor" href="#平面设计和ui设计有什么区别" aria-label="Permalink to &quot;平面设计和UI设计有什么区别？&quot;">​</a></h1><ol><li><p><strong>定义不同</strong><br><strong>平面设计</strong>：也称为视觉传达设计，是以视觉作为沟通和表现的方式，透过多种方式来创造和结合符号、图片和文字，借此作出用来传达想法或讯息的视觉表现。<br><strong>UI设计</strong>：是指对软件的人机交互、操作逻辑、界面美观的整体设计。</p></li><li><p><strong>设计对象不同</strong><br><strong>平面设计</strong>师：利用字体排印、视觉艺术、版面、电脑软件等方面的专业技巧，来达成创作计划的目的。<br><strong>UI设计</strong>：重点是设计人机交互的界面，设计的对象通常为软件产品的外形。</p></li><li><p><strong>设计用途不同</strong><br><strong>平面设计</strong>：是沟通传播、风格化和通过文字和图像解决问题的艺术。<br><strong>UI设计</strong>：是为软件的操作变得舒适简单、自由，便于充分体现软件的定位和特点。</p></li></ol><h2 id="ue-ux" tabindex="-1">UE/UX <a class="header-anchor" href="#ue-ux" aria-label="Permalink to &quot;UE/UX&quot;">​</a></h2><p>用户体验（User Experience，简称UE/UX）是用户在使用产品过程中建立起来的一种纯主观感受。但是对于一个界定明确的用户群体来讲，其用户体验的共性是能够经由良好设计实验来认识到。计算机技术和互联网的发展，使技术创新形态正在发生转变，以用户为中心、以人为本越来越得到重视，用户体验也因此被称做创新2.0模式的精髓。在中国面向知识社会的创新2.0——应用创新园区模式探索中，更将用户体验作为“三验”创新机制之首。</p><h2 id="ui设计与平面设计最大的区别" tabindex="-1">UI设计与平面设计最大的区别 <a class="header-anchor" href="#ui设计与平面设计最大的区别" aria-label="Permalink to &quot;UI设计与平面设计最大的区别&quot;">​</a></h2><p>UI：会动、有交互 平面：不会动</p><h2 id="平面设计" tabindex="-1">平面设计 <a class="header-anchor" href="#平面设计" aria-label="Permalink to &quot;平面设计&quot;">​</a></h2><p>认知、偏见： 海报、广告、杂质、包装</p>',8),n=[s];function i(l,_,d,h,p,c){return t(),a("div",null,n)}const g=r(o,[["render",i]]);export{U as __pageData,g as default};
